
<template>
  <div>
    <headTop :head="head">
      <img slot="left" src="../../images/icon-return.png" @click="customBack"/>
    </headTop>
    <div class="contentBox" v-cloak>
      <div class="topSearch" v-show="isShowSearch">
        <search></search>
      </div>
      <div :style="topTitleStyle" v-if="topTitle.length > 0">{{topTitle}}</div>
      <div class="allChooseView whiteBg clb" @click="chooseAll" v-if="isShowChooseAll">
        <img src="./images/icon_choosed.png" v-if="isChoosedAll"/>
        <img src="./images/icon_choosed_notAll.png" v-else-if="isChoosedPerson"/>
        <img src="./images/icon_choosed_not.png" v-else/>
        <span>全选</span>
      </div>
      <div v-if="isShowChineseSort">
        <div :style="dataContent">
          <div class="city" v-for="letter in letterSortList">
            <div class="city-title" :id="letter">{{letter}}</div>
            <div class="city-content">
              <ul class="city-list">
                <li class="city-item" v-for="(person,index) in letterNameList" v-if="person.capitalize === letter" @click="choosePerson(person,index)">
                <span class="selectView">
                    <img src="./images/icon_choosed.png" v-if="person.isSelected"/>
                    <img src="./images/icon_choosed_not.png" v-else/>
                </span>
                  <img class="headView" :src="person.avatar" v-if="person.avatar">
                  <img class="headView" src="./images/icon-default-personAvatar.png" v-else>
                  <span class="nameLabel">{{person.username}}</span>
                  <span class="moreMsgLabel">{{person.moreMsg}}</span>
                </li>
              </ul>
            </div>
          </div>
        </div>
        <!--点击右边索引出现的字母视图-->
        <div id="letter" v-if="isShowChineseSort"></div>
        <!--右边索引视图-->
        <div class="initials" v-if="isShowChineseSort">
          <ul id="characterList">
            <li @click="moveScroll()"><img src="./images/icon-searchIcon.png"></li>
            <li v-for="character in letterSortList" @click="moveScroll(character)">{{ character }}</li>
          </ul>
        </div>
      </div>
      <div v-else>
        <!--不使用通讯录的模式-->
        <div class="city-content whiteBg">
          <ul class="city-list">
            <li class="city-item" v-for="(person,index) in letterNameList" @click="choosePerson(person,index)">
                <span class="selectView">
                    <img src="./images/icon_choosed.png" v-if="person.isSelected"/>
                    <img src="./images/icon_choosed_not.png" v-else/>
                </span>
              <img class="headView" :src="person.avatar" v-if="person.avatar">
              <img class="headView" src="./images/icon-default-personAvatar.png" v-else>
              <span class="nameLabel">{{person.username}}</span>
              <span class="moreMsgLabel">{{person.moreMsg}}</span>
            </li>
          </ul>
        </div>

      </div>
      <!--底部选择人的头像视图-->
      <div ref="bottomBar" class="choosePerBottomView" v-if="isShowBottomBar">
        <scroller lock-y scrollbar-x class="bottomScrollView">
          <div :style="{width:hasChoosedPersonArr.length * 2 + 'rem',position:'relative',height:'1.5rem',marginTop:'0.35rem'}">
            <span class="bottomChoosedPerItem" v-for="(person,index) in hasChoosedPersonArr"  @click="choosePerson(person,index)">
                <img class="headView" :src="person.avatar" v-if="person.avatar">
                <img class="headView" src="./images/icon-default-personAvatar.png" v-else>
                <img class="headViewDelete" src="./images/icon-delete.png"/>
            </span>
          </div>
        </scroller>
        <div class="clb" :style="{display:'inline-block'}"></div>
        <span class="makeSureButton" @click="makeSureClick">确定</span>
      </div>
    </div>
  </div>
</template>

<script>
  import $ from 'jquery';
  import Search from 'vux/src/components/search/index.vue'
  import headTop from '../../components/Head.vue'
  import Scroller from 'vux/src/components/scroller/index.vue'

  export default {
    name: "index",
