93": "PB", "33071": "PF", "33098": "XL", "33100": "YA", "33152": "PB", "33261": "CX", "33324": "BP", "33333": "TD", "33406": "YA", "33426": "WM", "33432": "PB", "33445": "JG", "33486": "ZN", "33493": "TS", "33507": "QJ", "33540": "QJ", "33544": "ZC", "33564": "XQ", "33617": "YT", "33632": "QJ", "33636": "XH", "33637": "YX", "33694": "WG", "33705": "PF", "33728": "YW", "33882": "SR", "34067": "WM", "34074": "YW", "34121": "QJ", "34255": "ZC", "34259": "XL", "34425": "JH", "34430": "XH", "34485": "KH", "34503": "YS", "34532": "HG", "34552": "XS", "34558": "YE", "34593": "ZL", "34660": "YQ", "34892": "XH", "34928": "SC", "34999": "QJ", "35048": "PB", "35059": "SC", "35098": "ZC", "35203": "TQ", "35265": "JX", "35299": "JX", "35782": "SZ", "35828": "YS", "35830": "E", "35843": "TD", "35895": "YG", "35977": "MH", "36158": "JG", "36228": "QJ", "36426": "XQ", "36466": "DC", "36710": "JC", "36711": "ZYG", "36767": "PB", "36866": "SK", "36951": "YW", "37034": "YX", "37063": "XH", "37218": "ZC", "37325": "ZC", "38063": "PB", "38079": "TD", "38085": "QY", "38107": "DC", "38116": "TD", "38123": "YD", "38224": "HG", "38241": "XTC", "38271": "ZC", "38415": "YE", "38426": "KH", "38461": "YD", "38463": "AE", "38466": "PB", "38477": "XJ", "38518": "YT", "38551": "WK", "38585": "ZC", "38704": "XS", "38739": "LJ", "38761": "GJ", "38808": "SQ", "39048": "JG", "39049": "XJ", "39052": "HG", "39076": "CZ", "39271": "XT", "39534": "TD", "39552": "TD", "39584": "PB", "39647": "SB", "39730": "LG", "39748": "TPB", "40109": "ZQ", "40479": "ND", "40516": "HG", "40536": "HG", "40583": "QJ", "40765": "YQ", "40784": "QJ", "40840": "YK", "40863": "QJG" },
        title:'城市列表',
        letterSortList: '',
      }
    },
    // mixins: [zlMixins],
    mounted () {
      this.init();
    },
    computed:{
      topTitleStyle:function () {
        let dis = 0;
        if (this.isShowSearch) dis += 48;
        return {
          position:'relative',
          top:dis + 'px',
          color: 'red',
          marginLeft:0.75 + 'rem'
        }
      },
      dataContent() {
        let distance = 0;
        if (this.isShowSearch) distance += 48;
        if (this.topTitle.length > 0) distance += 16;
        return {
          position:'relative',
          top:distance + 'px',
          marginBottom:this.isShowBottomBar ? '2rem' : 0,
          width:'100%'
        }
      },
    },
    methods: {
      //确定按钮点击
      makeSureClick(){
        this.$emit('on-makeSure',this.hasChoosedPersonArr);
      },
      //点击全选
      chooseAll(){
        this.isChoosedPerson = false;
        this.isChoosedAll = !this.isChoosedAll;
        //点击全选
        if(this.isChoosedAll){
            for(let i = 0;i <this.letterNameList.length ;i++){
              this.letterNameList[i].isSelected = true;
            }
            this.hasChoosedPersonArr = this.letterNameList;
            return;
          }
        //取消全选
        for(let j = 0;j <this.letterNameList.length ;j++){
          this.letterNameList[j].isSelected = false;
        }
        this.hasChoosedPersonArr = [];
      },
      customBack(){
        this.$emit('on-goBack',null);
      },
      //选择人
      choosePerson(person,index){
        //index有两个数组，不能使用这个参数
        let personId = person.account_id;
        let isSelected = person.isSelected;
        person.isSelected = !isSelected;
        if(person.isSelected){
          this.hasChoosedPersonArr.push(person);
        }else{
          for(let k=0;k < this.hasChoosedPersonArr.length;k++){
             if (this.hasChoosedPersonArr[k].account_id == person.account_id){
               this.hasChoosedPersonArr.splice(k,1);
               break;
             }
          }
          for(let i = 0;i <this.letterNameList.length ;i++){
            if (this.letterNameList[i].account_id == person.account_id){
              this.letterNameList[i].isSelected = false;
              break;
            }
          }
        }
        this.isChoosedPerson = this.hasChoosedPersonArr.length > 0 ? true : false;
      },
      init(){
        let obj = {};
        let arr = [];
        const self = this;
        if(this.lastChoosedPersonArr.length > 0){
          this.hasChoosedPersonArr = this.lastChoosedPersonArr;
        }
        this.letterNameList.forEach(function(value,index){
          //在每个城市加一个capitalize的标识代表首字母
          value.capitalize = self.makePy(value.username)[0].toUpperCase().substr(0,1);
          if(!obj[value.capitalize]){
            obj[value.capitalize] = true;
            arr.push(value.capitalize);
            value.isSelected = false;
          }
        });
        //判断有无选择的人
        let choosedPerIdArr = [];
        for (let k=0;k < this.lastChoosedPersonArr.length;k++){
          choosedPerIdArr.push(this.lastChoosedPersonArr[k].account_id);
        }
        for (let i = 0;i < this.letterNameList.length; i++){
          let pId = this.letterNameList[i].account_id;
          if (choosedPerIdArr.includes(pId)){
            this.letterNameList[i].isSelected = true;
            debugger
          }else {
            this.letterNameList[i].isSelected = false;
          }
        }
        //字母列表排序
        this.letterSortList = arr.sort();
      },
      //所选字母淡入淡出效果
      moveScroll(character){
        //jq id为letter的元素
        const LetterBox=$('#letter');
        //jq class为initials的元素
        const Initials=$('.initials');
        Initials.css('background','rgba(145,145,145,0.6)');

        if(self.timeOut){
          clearTimeout(self.timeOut);
        }
        self.timeOut = setTimeout(function(){
          Initials.css('background','rgba(145,145,145,0)');
          LetterBox.fadeOut();
        },1000);
        if(character==undefined){
          $('html,body').animate({scrollTop: '0px'}, 300);//点击第一个滚到顶部
        }else{
          if($('#'+character).length>0){
            LetterBox.html(character).fadeIn();
            const LetterTop = $('#'+character).position().top;
            $('html,body').animate({scrollTop: LetterTop-46+'px'}, 300);
          }
        }
      },
      makePy(str) {
        if (typeof (str) != "string")
          throw new Error(-1, "函数makePy需要字符串类型参数!");
        let arrResult = new Array(); //保存中间结果的数组
        for (let i = 0, len = str.length; i < len; i++) {
          //获得unicode码
          let ch = str.charAt(i);
          //检查该unicode码是否在处理范围之内,在则返回该码对映汉字的拼音首字母,不在则调用其它函数处理
          arrResult.push(this.checkCh(ch));
        }
        //处理arrResult,返回所有可能的拼音首字母串数组
        return this.mkRslt(arrResult);
      },
      checkCh(ch) {
        let uni = ch.charCodeAt(0);
        //如果不在汉字处理范围之内,返回原字符,也可以调用自己的处理函数
        if (uni > 40869 || uni < 19968)
          return ch; //dealWithOthers(ch);
        //检查是否是多音字,是按多音字处理,不是就直接在this.strChineseFirstPY字符串中找对应的首字母
        return (this.oMultiDiff[uni] ? this.oMultiDiff[uni] : (this.strChineseFirstPY.charAt(uni - 19968)));
      },
      mkRslt(arr) {
        let arrRslt = [""];
        for (let i = 0, len = arr.length; i < len; i++) {
          let str = arr[i];
          let strlen = str.length;
          if (strlen == 1) {
            for (let k = 0; k < arrRslt.length; k++) {
              arrRslt[k] += str;
            }
          } else {
            let tmpArr = arrRslt.slice(0);
            arrRslt = [];
            for (let k = 0; k < strlen; k++) {
              //复制一个相同的arrRslt
              let tmp = tmpArr.slice(0);
              //把当前字符str[k]添加到每个元素末尾
              for (let j = 0; j < tmp.length; j++) {
                tmp[j] += str.charAt(k);
              }
              //把复制并修改后的数组连接到arrRslt上
              arrRslt = arrRslt.concat(tmp);
            }
          }
        }
        return arrRslt;
      }
    }
  }
</script>
<style>
  .city .city-title {
    font-size: 18px;
    font-weight: bold;
    padding: 5px 10px;
  }
  .city .city-content {
    background-color: #fff;
  }
  .city .city-list .city-item {
    border-bottom: 1px solid #eee;
    color: #333;
    border: 1px solid green;
  }

  #letter{
    width: 100px;
    height: 100px;
    border-radius: 5px;
    font-size: 75px;
    color: #555;
    text-align: center;
    line-height: 100px;
    background: rgba(145,145,145,0.6);
    position: fixed;
    left: 50%;
    top: 50%;
    margin:-50px 0px 0px -50px;
    z-index: 99;
    display: none;
  }
  #characterList {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
  }
  .initials{
    position: fixed;
    top: 47px;
    right: 0px;
    height: 100%;
    width: 16px;
    text-align: center;
    font-size: 12px;
    z-index: 99;
    /*background: white !important;*/
  }
  .initials li img{
    width: 14px;
  }

  /*
    -----------------
  */
  .topSearch{position: fixed;top:2.1rem;left: 0;width: 100%;background-color: white;z-index: 999}
  .city-item .headView{width: 50px;height: 50px;border-radius: 25px;}
  .city-item .nameLabel{position: relative;top:-0.75rem;max-width: 5rem;line-height: 100%;display:inline-block;font-size: 0.9rem}
  .city-item .moreMsgLabel{font-size: 0.6rem;color: rgb(170,170,170);position: relative;
    top: -0.7rem;}

  .choosePerBottomView {position: fixed;bottom: 0;left: 0;width: 100%;height: 2.2rem;background-color: white;}
  .choosePerBottomView .bottomScrollView {width: 80%;height: 2.2rem}
  .city-item .selectView img{width: 1rem;height: 1rem;}
  .city-item .selectView{position: relative;top:-0.9rem;margin-left: 0.75rem;display: inline-block;}
  .bottomChoosedPerItem img{width: 1.5rem;height: 1.5rem;position: relative;border-radius: 0.75rem;}
  .bottomChoosedPerItem {float:left;background-color: white;position: relative;display: inline-block;margin-left: 0.5rem;}
  .bottomChoosedPerItem .headViewDelete{position:absolute;top: 0;right: 0;width: 0.7rem;height: 0.7rem;}
  .whiteBg {background-color: white;}

  .allChooseView span{line-height: 0.8rem;font-size: 0.8rem;margin: 0.75rem;float: left}
  .allChooseView img{width: 1rem;height: 1rem;float: left;margin-top: 0.6rem;margin-left: 0.75rem}
  .allChooseView {margin-bottom: 2rem}

  .clb:after{content:'';display: block;clear: both;}
  .makeSureButton {background-color: #4685ff;color: white;border-radius: 0.3rem;width: 3rem;display: inline-block;padding-left: 0.35rem;position: relative;left: 80%;top:-1.8rem;}
</style>
